graphql.query:
query($characterId:Int!, $episodeId:Int!){
  
  character(characterId: $characterId)
  {
    id
    name
    gender
    status
  }
  
  location(locationId: 8)
  {
    name
    dimension
  }
  
  episode(episodeId: $episodeId)
  {
    name
    air_date
    episode
  }
  
  characters(filters: {name:"Rahul"})
  {
    info
    {
      count
    }
    result
    {
      name
      type
    }
  }
  
  episodes(filters:{episode:"hulu"})
  {
    result
    {
      id
      name
      air_date
      episode
    }
  }
  
}

mutation($locationName:String!, $characterName:String!, $episodeName:String!)
{
  createLocation(location:{name:$locationName, type:"Southzone", dimension:"123"})
  {
    id
  }
  
  createCharacter(character:{name:$characterName, type:"Macho", status:"Dead", species:"Fantasy", gender:"male", image:"png", locationId:27400, originId:27400})
  {
    id
  }
  
  createEpisode(episode:{name:$episodeName, air_date:"2026 January", episode:"Netflix"})
  {
    id
  }
  
  deleteLocations(locationIds:[27403,27404])
  {
    locationsDeleted
  }
}
